import React, { Component } from 'react'
import { Button, Input } from 'semantic-ui-react'
import axios from 'axios'

import PokemonView from './PokemonView/PokemonView'

import { Pokemon as PokemonCss, PokemonHeader } from './Pokemon.module.scss'

class Pokemon extends Component {

  constructor() {
    super();

    this.state = {
      value: '',
      pokemon: {},
    };

    this.baseUrl = 'http://localhost:8000/reviews/';

    this.inputChangeHandler = this.inputChangeHandler.bind(this);
    this.clickHandler = this.clickHandler.bind(this);
  }

  clickHandler() {
    // Form the URL
    let url = `${this.baseUrl}${this.state.value}`;

    // GET some data back!
    axios.get(url).then((response) => {
      console.log(response);

      this.setState({
        pokemon: JSON.parse(response.data)
      });

      console.log(this.state.pokemon);
    }).catch((error) => {
      console.log(error);
    });
  }

  inputChangeHandler(event) {
    this.setState({ value: event.target.value });
  }

  render() {
    if(this.state.pokemon) {
      var hi = this.state.pokemon.map((review) => {
        return (
          <li>
            {review.movie}
          </li>
          );
      }  
      );
    }

    return (
      <div className={PokemonCss}>
        <h1 className={PokemonHeader}>Let's GET us a Pokemon!</h1>

        <Input
          onChange={this.inputChangeHandler}
          label='https://pokeapi.co/api/v2/pokemon/'
          placeholder='Pokemon # here!'
          value={this.state.value}
        />
        <Button onClick={this.clickHandler}>
          GET
        </Button>

        {this.state.pokemon.Movie}
      </div>
    )
  }

}

export default Pokemon
