import React, { Component } from 'react';
import {Navbar, Nav} from 'react-bootstrap';
import NavBar from './NavBar.jsx';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class Profile extends Component {
  constructor() {
  super();

  this.state = {
    queryList: [],
    list: [],
    userId: ''
  };

  this.baseUrl = 'http://localhost:8000/users/';
  this.renderAgain();
  }
  renderAgain() {
    axios.get(this.baseUrl + this.state.userId).then((response) => {
    this.setState({
      queryList: response.movie_list
    });
    })
    if(!this.state.queryList) {
      this.setState({
        list: this.state.queryList.split(',')
      })
    }
  }


  render() {
    return (
      <li>
      <Navbar bg="light" expand="lg">
        <NavBar />
      </Navbar>
      <div class="btn-group-vertical">
        <Link to="/recommend">
          <button type="button" class="btn btn-secondary">
              Recommend
          </button>
        </Link>
        <Link to="/chat_room">
          <button type="button" class="btn btn-secondary">
              Chat Room
          </button>
        </Link>
      </div>
      <div class="container-fluid">
        <h3 class="text-center">Wish List</h3>
        {this.state.list.map(item => <div key={item.id}>{item.title}</div>)}
      </div>
    </li>


    )
  }
}
