import React, { Component } from 'react'
import { Button, Input } from 'semantic-ui-react'
import axios from 'axios'

import PokemonView from './PokemonView/PokemonView'

import { Pokemon as PokemonCss, PokemonHeader } from './Pokemon.module.scss'

class Pokemon extends Component {

  constructor() {
    super();

    this.state = {
      value: '',
      pokemon: {},
      reviewsList: '',
      content: ''
    };

    this.baseUrl = 'http://localhost:8000/reviews/';

    this.inputChangeHandler_movie = this.inputChangeHandler_movie.bind(this);
    this.clickHandler = this.clickHandler.bind(this);
    axios.get(this.baseUrl).then((response) => {
      // console.log(response);

      this.setState({
        pokemon: response.data,
        reviewsList: response.data.map((review) => {
          return (
            <li>
              {review.Movie}
